import "./App.css";
import GeneralInfo from "./components/GeneralInfo";
import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      phone: "",
    };
    this.handleGeneralInfoSubmit = this.handleGeneralInfoSubmit.bind(this);
    this.handleGeneralInfoEdit = this.handleGeneralInfoEdit.bind(this);
  }

  handleGeneralInfoSubmit(data) {
    this.setState({
      name: data.name,
      email: data.email,
      phone: data.phone,
      generalInfo: {},
      isEditing: false,
    });
  }

  handleGeneralInfoEdit() {
    this.setState({
      isEditing: true,
    });
  }
  render() {
    return (
      <div>
        <h2>CV MAKER</h2>
        <GeneralInfo
          onSubmit={this.handleGeneralInfoSubmit}
          onEdit={this.handleGeneralInfoEdit}
          data={{
            name: this.state.name,
            email: this.state.email,
            phone: this.state.phone,
          }}
          isEditing={this.state.isEditing}
        />
        {this.state.isEditing ? (
          <form onSubmit={this.handleGeneralInfoSubmit}>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={this.state.name}
                onChange={this.handleInputChange}
              />
            </label>
            <label>
              Email:
              <input
                type="text"
                name="email"
                value={this.state.email}
                onChange={this.handleInputChange}
              />
            </label>
            <label>
              Phone:
              <input
                type="text"
                name="phone"
                value={this.state.phone}
                onChange={this.handleInputChange}
              />
            </label>
            <button type="submit">Save</button>
          </form>
        ) : (
          <div>
            <p>Name: {this.state.name}</p>
            <p>Email: {this.state.email}</p>
            <p>Phone: {this.state.phone}</p>
            <button onClick={this.handleGeneralInfoEdit}>Edit</button>
          </div>
        )}
      </div>
    );
  }
}

export default App;
