import React from "react";
import GeneralInfoForm from "./components/GeneralInfoForm";
import Education from "./components/Education";
import PraticalExperience from "./components/PraticalExperience";
import "./App.css";
import Display from "./components/DataDisplay";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentGeneral: [],
      educationList: [],
    };
  }

  handleInfoListChange = (currentGeneral) => {
    this.setState({ currentGeneral }, () => {
      console.log("App state after currentgeneral change:", this.state);
    });
  };

  handleEducationDataAdd = (educationDataList) => {
    this.setState({ educationList: educationDataList }, () => {
      console.log("App state after educationlist change:", this.state);
    });
  };

  render() {
    const { educationList } = this.state;

    return (
      <div className="mainDiv">
        <GeneralInfoForm onInfoListChange={this.handleInfoListChange} />
        <Education onEducationDataAdd={this.handleEducationDataAdd} />
        <PraticalExperience />
        <Display
          currentGeneral={this.state.currentGeneral}
          educationDataList={educationList}
        />
      </div>
    );
  }
}

export default App;
