import "./App.css";
import GeneralInfo from "./components/GeneralInfo";
import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      phone: "",
      isSubmitted: false,
    };
    this.handleGeneralInfoSubmit = this.handleGeneralInfoSubmit.bind(this);
    this.handleGeneralInfoEdit = this.handleGeneralInfoEdit.bind(this);
  }

  handleGeneralInfoSubmit(data) {
    this.setState({
      name: data.name,
      email: data.email,
      phone: data.phone,
      isSubmitted: true,
    });
  }

  handleGeneralInfoEdit() {
    this.setState({
      isSubmitted: false,
    });
  }

  render() {
    return (
      <div>
        <h2>CV MAKER</h2>
        <GeneralInfo
          onSubmit={this.handleGeneralInfoSubmit}
          isSubmitted={this.state.isSubmitted}
        />
        <p>Name: {this.state.name}</p>
        <p>Email: {this.state.email}</p>
        <p>Phone: {this.state.phone}</p>
        {this.state.isSubmitted && (
          <button onClick={this.handleGeneralInfoEdit}>Edit</button>
        )}
      </div>
    );
  }
}

export default App;
