import "./App.css";
import GeneralInfo from "./components/GeneralInfo";
import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      phone: "",
      isSubmitted: false,
    };
    this.handleGeneralInfoSubmit = this.handleGeneralInfoSubmit.bind(this);
    this.handleGeneralInfoEdit = this.handleGeneralInfoEdit.bind(this);
  }

  handleGeneralInfoSubmit(data) {
    this.setState({
      name: data.name,
      email: data.email,
      phone: data.phone,
      isSubmitted: true,
    });
  }

  handleGeneralInfoEdit() {
    this.setState({
      isSubmitted: false,
    });
  }

  render() {
    return (
      <div>
        <h2>CV MAKER</h2>
        {this.state.isEditing ? (
          <GeneralInfo
            onSubmit={this.handleGeneralInfoSubmit}
            data={this.state}
          />
        ) : (
          <div>
            <p>Name: {this.state.name}</p>
            <input
              type="text"
              value={this.state.name}
              name="name"
              onChange={this.handleInputChange}
            />
            <p>Email: {this.state.email}</p>
            <input
              type="text"
              value={this.state.email}
              name="email"
              onChange={this.handleInputChange}
            />
            <p>Phone: {this.state.phone}</p>
            <input
              type="text"
              value={this.state.phone}
              name="phone"
              onChange={this.handleInputChange}
            />
            <button onClick={this.handleGeneralInfoEdit}>Edit</button>
          </div>
        )}
      </div>
    );
  }
}

export default App;
