import React from "react";

class GeneralInfo extends React.Component {
  constructor(props) {
    super(props);

    const { name, email, phone } = this.props;

    this.state = {
      name: name || "",
      email: email || "",
      phone: phone || "",
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidUpdate(prevProps) {
    const { name, email, phone } = this.props;

    if (
      prevProps.name !== name ||
      prevProps.email !== email ||
      prevProps.phone !== phone
    ) {
      this.setState({
        name: name || "",
        email: email || "",
        phone: phone || "",
      });
    }
  }

  handleInputChange(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.props.onSubmit(this.state);
  }

  render() {
    const { isSubmitted, isEdit } = this.props;
    const { name, email, phone } = this.state;

    if (isSubmitted && !isEdit) {
      return null; // Render nothing if form is already submitted and not in edit mode
    }

    return (
      <div className="infoDiv">
        <h2>GENERAL INFO</h2>
        <form onSubmit={this.handleSubmit}>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={name}
              onChange={this.handleInputChange}
              disabled={isSubmitted && !isEdit} // Disable input if form is submitted and not in edit mode
            />
          </label>
          <br />
          <label>
            Email:
            <input
              type="text"
              name="email"
              value={email}
              onChange={this.handleInputChange}
              disabled={isSubmitted && !isEdit} // Disable input if form is submitted and not in edit mode
            />
          </label>
          <label>
            Phone:
            <input
              type="text"
              name="phone"
              value={phone}
              onChange={this.handleInputChange}
              disabled={isSubmitted && !isEdit} // Disable input if form is submitted and not in edit mode
            />
          </label>
          {isSubmitted && (
            <button type="button" onClick={this.props.onCancel}>
              Cancel
            </button>
          )}
          <button type="submit">{isSubmitted ? "Save" : "Submit"}</button>
        </form>
      </div>
    );
  }
}

export default GeneralInfo;
