import React from "react";
import GeneralInfoForm from "./components/GeneralInfoForm";
import Education from "./components/Education";
import PraticalExperience from "./components/PraticalExperience";
import DataDisplay from "./components/DataDisplay";
import "./App.css";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      phone: "",
      city: "",
    };
  }

  // Define callback functions to update the state with data from child components
  handleGeneralInfoChange = (name, email, phone, city) => {
    this.setState({ name, email, phone, city });
  };

  // Render the child components and pass down the data and callback functions as props
  render() {
    const { name, email, phone, city } = this.state;
    return (
      <div className="mainDiv">
        <GeneralInfoForm onGeneralInfoChange={this.handleGeneralInfoChange} />
        <Education />
        <PraticalExperience />
        <DataDisplay name={name} email={email} phone={phone} city={city} />
      </div>
    );
  }
}

export default App;
