import React from "react";

class GeneralInfoForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      currentGeneral: {
        name: "",
        email: "",
        phone: "",
        city: "",
      },
      infoList: [],
      submitted: false,
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleAdd = this.handleAdd.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState((prevState) => {
      const currentGeneral = { ...prevState.currentGeneral };
      currentGeneral[name] = value;
      return { currentGeneral };
    });
  }

  handleAdd() {
    this.setState((prevState) => ({
      infoList: [...prevState.infoList, prevState.currentGeneral],
      currentGeneral: {
        name: "",
        email: "",
        phone: "",
        city: "",
      },
      submitted: true,
    }));
  }

  handleDelete(index) {
    const stateToCopy = { ...this.state };
    const newInfoList = [...stateToCopy.infoList];
    newInfoList.splice(index, 1);
    stateToCopy.infoList = newInfoList;
    stateToCopy.submitted = false;
    this.setState(stateToCopy);
  }

  handleEdit(index) {
    const { infoList } = this.state;
    const infoToEdit = infoList[index];
    const uptadetInfoList = [...infoList];
    uptadetInfoList.splice(index, 1);
    this.setState({
      educationList: uptadetInfoList,
      currentEducation: infoToEdit,
      submitted: false,
    });
  }

  render() {
    const { infoList, currentGeneral } = this.state;

    return (
      <div className="educationDiv">
        <h2>GENERAL INFO</h2>
        {infoList.length > 0 ? (
          <ul className="educationList">
            {infoList.map((info, index) => (
              <li key={index}>
                <p>
                  Name: {info.name}
                  <br />
                  Email: {info.email}
                  <br />
                  Phone: {info.phone}
                  <br />
                  City: {info.city}
                </p>
                <button onClick={() => this.handleEdit(index)}>Edit</button>
                <button onClick={() => this.handleDelete(index)}>Delete</button>
              </li>
            ))}
          </ul>
        ) : (
          <p>No info added yet</p>
        )}
        <form>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={currentGeneral.school}
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            Email:
            <input
              type="text"
              name="email"
              value={currentGeneral.email}
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            Phone:
            <input
              type="text"
              name="phone"
              value={currentGeneral.phone}
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            City:
            <input
              type="text"
              name="city"
              value={currentGeneral.city}
              onChange={this.handleInputChange}
            />
          </label>

          <button
            type="button"
            onClick={this.handleAdd}
            disabled={this.state.submitted}
          >
            Add info
          </button>
        </form>
      </div>
    );
  }
}

export default GeneralInfoForm;
