import React from "react";
import GeneralInfo from "./components/GeneralInfo";
import Education from "./components/Education";
import "./App.css";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      phone: "",
      isSubmitted: false,
      isEdit: false,
    };
    this.handleGeneralInfoSubmit = this.handleGeneralInfoSubmit.bind(this);
    this.handleGeneralInfoEdit = this.handleGeneralInfoEdit.bind(this);
    this.handleGeneralInfoCancel = this.handleGeneralInfoCancel.bind(this);
  }

  handleGeneralInfoSubmit(data) {
    this.setState({
      name: data.name,
      email: data.email,
      phone: data.phone,
      isSubmitted: true,
      isEdit: false,
    });
  }

  handleGeneralInfoEdit() {
    this.setState({
      isEdit: true,
    });
  }

  handleGeneralInfoCancel() {
    this.setState({
      isEdit: false,
    });
  }

  render() {
    const { name, email, phone, isSubmitted, isEdit } = this.state;

    return (
      <div>
        <h2>CV MAKER</h2>
        <GeneralInfo
          onSubmit={this.handleGeneralInfoSubmit}
          isSubmitted={isSubmitted}
          isEdit={isEdit}
          name={name}
          email={email}
          phone={phone}
          onCancel={this.handleGeneralInfoCancel}
        />
        {isSubmitted && (
          <>
            <p>Name: {name}</p>
            <p>Email: {email}</p>
            <p>Phone: {phone}</p>
            {!isEdit && (
              <button onClick={this.handleGeneralInfoEdit}>Edit</button>
            )}
          </>
        )}
        <Education />
      </div>
    );
  }
}

export default App;
