import React from "react";

class GeneralInfoForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      currentGeneral: {
        name: "",
        email: "",
        phone: "",
        city: "",
      },
      infoList: [],
      submitted: false,
      editMode: false,
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleAdd = this.handleAdd.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState((prevState) => {
      const currentGeneral = { ...prevState.currentGeneral };
      currentGeneral[name] = value;
      return { currentGeneral };
    });
  }

  handleAdd = (event) => {
    event.preventDefault();
    const { currentGeneral, infoList } = this.state;
    if (infoList.length > 0) {
      const newList = [...infoList];
      newList[0] = currentGeneral;
      this.setState({
        infoList: newList,
        submitted: true,
        currentGeneral: {
          name: "",
          email: "",
          phone: "",
          city: "",
        },
      });
    } else {
      this.setState({
        infoList: [currentGeneral],
        submitted: true,
        currentGeneral: {
          name: "",
          email: "",
          phone: "",
          city: "",
        },
      });
    }
  };

  handleEdit(index) {
    const { infoList } = this.state;
    const infoToEdit = infoList[index];
    this.setState({
      currentGeneral: infoToEdit,
      editMode: true,
      submitted: false,
    });
  }

  render() {
    const { infoList, currentGeneral, editMode } = this.state;

    return (
      <div className="educationDiv">
        <h2>GENERAL INFO</h2>
        <ul className="educationList">
          {infoList.map((info, index) => (
            <li key={index}>
              <div></div>

              <div>
                <p>
                  Name: {info.name}
                  <br />
                  Email: {info.email}
                  <br />
                  Phone: {info.phone}
                  <br />
                  City: {info.city}
                </p>
                <button onClick={() => this.handleEdit(index)}>Edit</button>
              </div>
            </li>
          ))}
        </ul>
        <form>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={currentGeneral.name}
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            Email:
            <input
              type="text"
              name="email"
              value={currentGeneral.email}
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            Phone:
            <input
              type="text"
              name="phone"
              value={currentGeneral.phone}
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            City:
            <input
              type="text"
              name="city"
              value={currentGeneral.city}
              onChange={this.handleInputChange}
            />
          </label>

          <button
            type="button"
            onClick={this.handleAdd}
            disabled={this.state.submitted}
          >
            Add info
          </button>
        </form>
      </div>
    );
  }
}

export default GeneralInfoForm;
