import React from "react";
import GeneralInfoForm from "./components/GeneralInfoForm";
import Education from "./components/Education";
import PraticalExperience from "./components/PraticalExperience";
import "./App.css";
import Display from "./components/DataDisplay";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      text: "ciao",
    };
  }

  handleInfoListChange = (infoList) => {
    // Update the state with infoList data
    this.setState({ infoList });
  };

  render() {
    return (
      <div className="mainDiv">
        <GeneralInfoForm onInfoListChange={this.handleInfoListChange} />
        <Education />
        <PraticalExperience />
        <Display text={this.state.text} />
      </div>
    );
  }
}

export default App;
