import React, { useState } from "react";

function GeneralInfo(props) {
  const [currentGeneral, setCurrentGeneral] = useState({
    name: "",
    email: "",
    phone: "",
    city: "",
    age: "",
  });

  const [updatedGeneral, setUpdatedGeneral] = useState({
    name: "",
    email: "",
    phone: "",
    city: "",
    age: "",
  });

  const handleInputChange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    setUpdatedGeneral((prevGeneral) => ({
      ...prevGeneral,
      [name]: value,
    }));
  };

  const handleAdd = () => {
    setCurrentGeneral(updatedGeneral);
    setUpdatedGeneral({
      name: "",
      email: "",
      phone: "",
      city: "",
      age: "",
    });
  };

  return (
    <div className="infoDiv">
      <h2>GENERAL INFO</h2>
      {currentGeneral.name !== "" ||
      currentGeneral.email !== "" ||
      currentGeneral.phone !== "" ||
      currentGeneral.city !== "" ||
      currentGeneral.age !== "" ? (
        <ul className="infoList">
          <p>
            Name: {currentGeneral.name}
            <br />
            Email: {currentGeneral.email}
            <br />
            Phone: {currentGeneral.phone}
            <br />
            City: {currentGeneral.city}
            <br />
            Age: {currentGeneral.age}
          </p>
          <button onClick={() => handleEdit(0)}>Edit</button>
        </ul>
      ) : (
        <p>No info added yet</p>
      )}
      <form className="formInfo">
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={currentGeneral.name}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Email:
          <input
            type="text"
            name="email"
            value={currentGeneral.email}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Phone:
          <input
            type="text"
            name="phone"
            value={currentGeneral.phone}
            onChange={handleInputChange}
          />
        </label>
        <label>
          City:
          <input
            type="text"
            name="city"
            value={currentGeneral.city}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Age:
          <input
            type="text"
            name="age"
            value={currentGeneral.age}
            onChange={handleInputChange}
          />
        </label>

        <button type="button" onClick={handleAdd}>
          Add info
        </button>
      </form>
    </div>
  );
}

export default GeneralInfo;
