import React from "react";
import GeneralInfoForm from "./components/GeneralInfoForm";
import Education from "./components/Education";
import PraticalExperience from "./components/PraticalExperience";
import "./App.css";
import Display from "./components/DataDisplay";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentGeneral: [],
      educationList: [],
    };
    console.log("App constructor state:", this.state);
  }

  handleInfoListChange = (currentGeneral) => {
    this.setState({ currentGeneral }, () => {
      console.log("App state after infoList change:", this.state);
    });
  };

  handleEducationDataAdd = (data) => {
    this.setState((prevState) => ({
      educationList: [...prevState.educationDataList, data],
    }));
  };

  render() {
    const { educationDataList } = this.state;

    return (
      <div className="mainDiv">
        <GeneralInfoForm onInfoListChange={this.handleInfoListChange} />
        <Education onEducationDataAdd={this.handleEducationDataAdd} />
        <PraticalExperience />
        <Display
          currentGeneral={this.state.currentGeneral}
          educationDataList={educationDataList}
        />
      </div>
    );
  }
}

export default App;
