import React from "react";

class GeneralInfoForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      currentGeneral: {
        name: "",
        email: "",
        phone: "",
        city: "",
      },
      infoList: [],
      submitted: false,
      editMode: false,
      editIndex: null,
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleAdd = this.handleAdd.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
    this.handleSave = this.handleSave.bind(this);
    this.handleCancelEdit = this.handleCancelEdit.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState((prevState) => {
      const currentGeneral = { ...prevState.currentGeneral };
      currentGeneral[name] = value;
      return { currentGeneral };
    });
  }

  handleAdd() {
    this.setState((prevState) => ({
      infoList: [...prevState.infoList, prevState.currentGeneral],
      currentGeneral: {
        name: "",
        email: "",
        phone: "",
        city: "",
      },
      submitted: true,
    }));
  }

  handleEdit(index) {
    const { infoList } = this.state;
    const infoToEdit = infoList[index];
    this.setState({
      currentGeneral: infoToEdit,
      editMode: true,
      editIndex: index,
    });
  }

  handleSave() {
    const { infoList, editIndex, currentGeneral } = this.state;
    const updatedInfoList = [...infoList];
    updatedInfoList[editIndex] = currentGeneral;
    this.setState({
      infoList: updatedInfoList,
      currentGeneral: {
        name: "",
        email: "",
        phone: "",
        city: "",
      },
      editMode: false,
      editIndex: null,
      submitted: false,
    });
  }

  handleCancelEdit() {
    this.setState({
      currentGeneral: {
        name: "",
        email: "",
        phone: "",
        city: "",
      },
      editMode: false,
      editIndex: null,
      submitted: false,
    });
  }

  render() {
    const { infoList, currentGeneral, isEditMode } = this.state;

    return (
      <div className="educationDiv">
        <h2>GENERAL INFO</h2>
        {infoList.length > 0 ? (
          <ul className="educationList">
            {infoList.map((info, index) => (
              <li key={index}>
                {isEditMode === index ? (
                  <div>
                    <form>
                      <label>
                        Name:
                        <input
                          type="text"
                          name="name"
                          value={currentGeneral.name}
                          onChange={this.handleInputChange}
                        />
                      </label>
                      <label>
                        Email:
                        <input
                          type="text"
                          name="email"
                          value={currentGeneral.email}
                          onChange={this.handleInputChange}
                        />
                      </label>
                      <label>
                        Phone:
                        <input
                          type="text"
                          name="phone"
                          value={currentGeneral.phone}
                          onChange={this.handleInputChange}
                        />
                      </label>
                      <label>
                        City:
                        <input
                          type="text"
                          name="city"
                          value={currentGeneral.city}
                          onChange={this.handleInputChange}
                        />
                      </label>
                      <button
                        type="button"
                        onClick={() => this.handleSave(index)}
                      >
                        Save
                      </button>
                    </form>
                  </div>
                ) : (
                  <div>
                    <p>
                      Name: {info.name}
                      <br />
                      Email: {info.email}
                      <br />
                      Phone: {info.phone}
                      <br />
                      City: {info.city}
                    </p>
                    <button onClick={() => this.handleEdit(index)}>Edit</button>
                  </div>
                )}
              </li>
            ))}
          </ul>
        ) : (
          <p>No info added yet</p>
        )}
        <form>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={currentGeneral.name}
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            Email:
            <input
              type="text"
              name="email"
              value={currentGeneral.email}
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            Phone:
            <input
              type="text"
              name="phone"
              value={currentGeneral.phone}
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            City:
            <input
              type="text"
              name="city"
              value={currentGeneral.city}
              onChange={this.handleInputChange}
            />
          </label>

          <button
            type="button"
            onClick={this.handleAdd}
            disabled={this.state.submitted}
          >
            Add info
          </button>
        </form>
      </div>
    );
  }
}

export default GeneralInfoForm;
