import React from "react";

class Education extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      educationList: props.educationList,
    };
  }

  handleInputChange(event, index) {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState((prevState) => {
      const educationList = [...prevState.educationList];
      educationList[index][name] = value;
      return { educationList };
    });
  }

  handleAdd() {
    const newEducation = {
      school: "",
      degree: "",
      fieldOfStudy: "",
      startDate: "",
      endDate: "",
    };

    this.setState((prevState) => ({
      educationList: [...prevState.educationList, newEducation],
    }));
  }

  render() {
    const { educationList } = this.state;

    return (
      <div>
        <h2>EDUCATION</h2>
        {educationList.map((item, index) => (
          <div key={index}>
            <p>School: {item.school}</p>
            <p>Degree: {item.degree}</p>
            <p>Field of study: {item.fieldOfStudy}</p>
            <p>Start: {item.startDate}</p>
            <p>End: {item.endDate}</p>
            <hr />
          </div>
        ))}
        <button onClick={() => this.handleAdd()}>Add new education</button>
      </div>
    );
  }
}

export default Education;
