import React from "react";
import GeneralInfo from "./components/GeneralInfo";
import GeneralInfoForm from "./components/GeneralInfoForm";
import Education from "./components/Education";
import PraticalExperience from "./components/PraticalExperience";
import "./App.css";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      phone: "",
      city: " ",
      isSubmitted: false,
      isEdit: false,
    };
    this.handleGeneralInfoSubmit = this.handleGeneralInfoSubmit.bind(this);
    this.handleGeneralInfoEdit = this.handleGeneralInfoEdit.bind(this);
    this.handleGeneralInfoCancel = this.handleGeneralInfoCancel.bind(this);
  }

  handleGeneralInfoSubmit(data) {
    this.setState({
      name: data.name,
      email: data.email,
      phone: data.phone,
      city: data.city,
      isSubmitted: true,
      isEdit: false,
    });
  }

  handleGeneralInfoEdit() {
    this.setState({
      isEdit: true,
    });
  }

  handleGeneralInfoCancel() {
    this.setState({
      isEdit: false,
    });
  }

  render() {
    const { name, email, phone, city, isSubmitted, isEdit } = this.state;

    return (
      <div className="mainDiv">
        <GeneralInfoForm />
        <Education />
        <PraticalExperience />
      </div>
    );
  }
}

export default App;
