import React from "react";

class Education extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      educationList: [
        {
          school: "",
          degree: "",
          fieldOfStudy: "",
          startDate: "",
          endDate: "",
        },
      ],
    };
  }

  handleInputChange(event, index) {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState((prevState) => {
      const educationList = [...prevState.educationList];
      educationList[index][name] = value;
      return { educationList };
    });
  }

  handleAdd() {
    const newEducation = {
      school: "",
      degree: "",
      fieldOfStudy: "",
      startDate: "",
      endDate: "",
    };

    this.setState((prevState) => ({
      educationList: [...prevState.educationList, newEducation],
    }));
  }

  render() {
    return (
      <div>
        <h2>EDUCATION</h2>
        {this.state.educationList.map((item, index) => (
          <form key={index}>
            <label>
              School:
              <input
                type="text"
                value={item.school}
                name="school"
                onChange={(event) => this.handleInputChange(event, index)}
              />
            </label>
            <label>
              Degree:
              <input
                type="text"
                value={item.degree}
                name="degree"
                onChange={(event) => this.handleInputChange(event, index)}
              />
            </label>
            <label>
              Field of study:
              <input
                type="text"
                value={item.fieldOfStudy}
                name="fieldOfStudy"
                onChange={(event) => this.handleInputChange(event, index)}
              />
            </label>
            <label>
              Start:
              <input
                type="text"
                value={item.startDate}
                name="startDate"
                onChange={(event) => this.handleInputChange(event, index)}
              />
            </label>
            <label>
              End:
              <input
                type="text"
                value={item.endDate}
                name="endDate"
                onChange={(event) => this.handleInputChange(event, index)}
              />
            </label>
          </form>
        ))}
        <button onClick={() => this.handleAdd()}>Add new education</button>
      </div>
    );
  }
}

export default Education;
