import React from "react";

class Education extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      currentEducation: {
        school: "",
        degree: "",
        fieldOfStudy: "",
        startDate: "",
        endDate: "",
      },
      educationList: [],
    };
  }

  handleInputChange(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    this.setState((prevState) => {
      const currentEducation = { ...prevState.currentEducation };
      currentEducation[name] = value;
      return { currentEducation };
    });
  }

  handleAdd() {
    this.setState((prevState) => ({
      educationList: [...prevState.educationList, prevState.currentEducation],
      currentEducation: {
        school: "",
        degree: "",
        fieldOfStudy: "",
        startDate: "",
        endDate: "",
      },
    }));
  }

  render() {
    return (
      <div>
        <h2>EDUCATION</h2>
        <form>
          <label>
            School:
            <input
              type="text"
              value={this.state.currentEducation.school}
              name="school"
              onChange={(event) => this.handleInputChange(event)}
            />
          </label>
          <label>
            Degree:
            <input
              type="text"
              value={this.state.currentEducation.degree}
              name="degree"
              onChange={(event) => this.handleInputChange(event)}
            />
          </label>
          <label>
            Field of study:
            <input
              type="text"
              value={this.state.currentEducation.fieldOfStudy}
              name="fieldOfStudy"
              onChange={(event) => this.handleInputChange(event)}
            />
          </label>
          <label>
            Start:
            <input
              type="text"
              value={this.state.currentEducation.startDate}
              name="startDate"
              onChange={(event) => this.handleInputChange(event)}
            />
          </label>
          <label>
            End:
            <input
              type="text"
              value={this.state.currentEducation.endDate}
              name="endDate"
              onChange={(event) => this.handleInputChange(event)}
            />
          </label>
        </form>
        <button onClick={() => this.handleAdd()}>Add new education</button>
      </div>
    );
  }
}

export default Education;
